import { Table } from 'react-bootstrap';
import React, { useEffect, useState } from "react";
import axios from 'axios';
import { useNavigate } from "react-router-dom";
import Button from 'react-bootstrap/Button';
import ApproveAdmin from './approveAdmin';
import ApproveUser from './approveUser';


function Approve() {

	//const location = useLocation()
	const navigate = useNavigate();

	const [rentedData, setRentedData] = useState([]) // 현재 대여중인 노트북 정보(관리자만 보기)
	const [user, setUser] = useState([]) //현재 로그인 사용자 정보
	const [apply, setApply] = useState([]) //노트북 대여 신청 목록담기
	const [userwanted, setUserWanted] = useState([]) //신청자 신청현황 db 저장



	useEffect(() => {
		axios.get('http://localhost:8081/userpage')
			.then((res1) => {
				setUser(res1.data)
			}).catch(() => {
				console.log("failed1")
			})
		//window.location.reload();
		if (user.id === 'admin') {
			axios.all([axios.get('http://localhost:8081/laptopdatabases'), axios.get('http://localhost:8081/rentapply')])
				.then(
					axios.spread((res2, res3) => {
						setRentedData(res2.data)
						setApply(res3.data)
					})
				).catch(() => {
					console.log("failed2")
				})

		} else {
			axios.get('http://localhost:8081/userwantedapply')
				.then((res4) => {
					setUserWanted(res4.data)
				}).catch(() => {
					console.log("failed3")
				})
		}
	}, [])

	console.log('admin로그인할때 보이는 승인대기목록', apply)
	console.log('로그인정보', user)
	console.log('userwanted', userwanted)
	console.log('노트북', rentedData)

	const applyBtn = async ([user]) => {
		console.log('승인버튼 누른 신청유저', user)

		try {
			await axios.post('http://localhost:8081/applybtn', user, {
				headers: { 'Content-Type': 'application/x-www-form-urlencoded' }
			})
			window.location.reload();
		} catch (e) {
			console.log('approve failed', e)
		}
	}

	const returnBtn = async ([returnUser]) => {
		console.log('반납처리된 유저', returnUser)

		try {
			await axios.post('http://localhost:8081/returnbtn', returnUser, {
				headers: { 'Content-Type': 'application/x-www-form-urlencoded' }
			})
		} catch (e) {
			console.log(e)
		}
	}

	return (

		<>
			{
				user.id === 'admin' //관리자 계정일때만 보이는 ui
					? <ApproveAdmin />
					: <ApproveUser />
			}
		</>

	)
}

export default Approve;
